export interface CartItem {
    coverImage: string,
    selectedSize: string,
    productId: string,
    price: number,
    name: string,
    quantity: number
}

const calculateSubtotal = (data: CartItem[]) => {
    return data.reduce(
        (accumulator, currentValue) => accumulator + currentValue.quantity * currentValue.price,
        0
    );
}

